module.exports.add = function (args, message) {
    let numbers = args.map(n => parseInt(n));
    let total = numbers.reduce((p, c) => p + c);
    message.channel.sendMessage(total);
};

module.exports.substract = function (args, message) {
        var numbers = args.map(n => parseInt(n));
        var total = numbers.reduce((p, c) => p - c);
        message.channel.sendMessage(total);
};

module.exports.multiply = function (args, message) {
        var numbers = args.map(n => parseInt(n));
        var total = numbers.reduce((p, c) => p * c);
        message.channel.sendMessage(total);
};

module.exports.divide = function (args, message) {
        var numbers = args.map(n => parseInt(n));
        var total = numbers.reduce((p, c) => p / c);
        message.channel.sendMessage(total);
};